import { useEffect, useState } from 'react';
import axios from "axios";
import { BsFillStarFill, BsStar, BsPencilSquare, BsFillTrashFill} from "react-icons/bs";
import DeleteRating from "./DeleteRating";
import UpdateRating from "./UpdateRating";

function Ratings() {
    const [ratings, setRatings] = useState("");

    const [updateRating, setUpdateRating] = useState(null);
    const [deleteRating, setDeleteRating] = useState(null);
    const [addRating, setAddRating] = useState(true);


    const user = localStorage.getItem("user");
    const loggedIn = localStorage.getItem("loggedin");


    useEffect(() => {
        axios.get("http://localhost/index.php/rating/get")
        .then((response) => {
            setRatings(response.data)
        })
        .catch(err => console.log(err));
    });

    if (!ratings) return null;

    const isUserRating = (rating) => {
        return user === rating.username;
      };

    const closeDelete = () => {
        setDeleteRating(null);
    }

    const stars = (rating) => {
        const max = 5;
        const stars = [];

        for(let i=0; i<max; i++) {
            if (i<rating) {
                stars[i] = <BsFillStarFill /**style={{color:'yellow'}}*//>;
            }
            else {
                stars[i] = <BsStar/>;
            }
        }
        return <div>{stars}</div>;
    };

    return(
        <div className="mainContainer">
            <h1>Ratings</h1>
            <div className={"titleContainer"} style={{display:'flex'}} >
                {ratings.map((rating) => (
                    <div style={{display:'flex'}} key={rating.id}>
                        <p style={{marginRight:'5px'}}>{rating.song} </p>
                        <p style={{marginRight:'5px'}}>{rating.artist} </p> 
                        <p style={{marginRight:'5px'}}> by {rating.username}</p> 
                        <p>{stars(rating.rating)}</p>
                        {true && (
                            <div style={{display:'flex'}} >
                                <UpdateRating ratingId={rating.id}/>
                                <BsFillTrashFill onClick={setDeleteRating(rating.id)}/>
                            </div>
                        )}
                        {(deleteRating === rating.id) && (
                            <DeleteRating ratingId={deleteRating.id} onDelete={() => closeDelete()} />
                        )}
                    </div>
                ))}
                {(loggedIn && addRating) && (
                    <div className="add-song-rating">
                        <strong>Add New Song Rating</strong>
                        <AddNewRating />
                    </div>
                    )}
            </div>
        </div>
    );
}

export default Ratings
