import { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import axios from 'axios';


function UpdateRating({song, artist, ratingId}) {
  const [inputs, setInputs] = useState({});
  const [modal, setModal] = useState(true);
  const [newRating, setNewRating] = useState("");


  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = async () => {
    await axios.delete(`${"http://localhost/index.php/rating/update"}?${ratingId}`, {
      body: {rating: newRating}
    })
    .then(() => setMessage("Rating successfully deleted!"))
    .catch(() => setMessage("There was a problem, deletion aborted."));
    setModal(false);
  }

  return (
    <Modal isOpen={modal}>
    <ModalHeader>Update Rating</ModalHeader>
    <ModalBody>
      <label>Song:</label>
      <span></span>
      <br/>
      <label>Artist:</label>
      <span></span>
      <br/>
      <label>Rating:
        <input 
          type="number" 
          name="rating" 
          value={inputs.rating || ""} 
          onChange={(e) => setNewRating(e.target.value)}
        />
      </label>
    </ModalBody>
    <ModalFooter>
      <Button color="primary" onClick={handleSubmit}>
        Delete
      </Button>{' '}
      <Button color="secondary" onClick={() => setModal(false)}>
        Cancel
      </Button>
    </ModalFooter>
  </Modal>
  )
}

export default UpdateRating;
