import { useEffect, useState } from 'react';
import axios from "axios";
import { BsFillStarFill, BsStar, BsPencilSquare, BsFillTrashFill} from "react-icons/bs";
import DeleteRating from "./DeleteRating";
import UpdateRating from "./UpdateRating";
import { Tooltip } from 'reactstrap';


function Ratings() {
    const [ratings, setRatings] = useState("");

    const [updateRating, setUpdateRating] = useState(null);
    const [deleteRating, setDeleteRating] = useState(null);
    const [tooltipOpen, setTooltipOpen] = useState(false);
    const toggle = () => setTooltipOpen(!tooltipOpen);


    const user = localStorage.getItem("user");


    useEffect(() => {
        axios.get("http://localhost/index.php/rating/get")
        .then((response) => {
            setRatings(response.data);
        })
        .catch(err => console.log(err));
    }, []);

    if (!ratings) return null;

    const isUserRating = (rating) => {
        return user === rating.username;
      };


    const stars = (rating) => {
        const max = 5;
        const stars = [];

        for(let i=0; i<max; i++) {
            if (i<rating) {
                stars[i] = <BsFillStarFill /**style={{color:'yellow'}}*//>;
            }
            else {
                stars[i] = <BsStar/>;
            }
        }
        return <div>{stars}</div>;
    };

    return(
        <div className="mainContainer">
            <h1>Ratings</h1>
            <div className={"titleContainer"}>
                {ratings.map((rating) => (
                    <div style={{display:'flex'}} key={rating.id}>
                        <p style={{marginRight:'5px'}}>{rating.song} </p>
                        <p style={{marginRight:'5px'}}>{rating.artist} </p> 
                        <p style={{marginRight:'5px'}}> by {rating.username}</p> 
                        <p>{stars(rating.rating)}</p>
                        <span>
                        {true && (
                            <div style={{display:'flex'}} >
                                <BsPencilSquare id='update-icon' ratingId={rating.id} onClick={() => setUpdateRating(rating.id)}/>
                                <Tooltip {...args} isOpen={tooltipOpen} target="update-icon" toggle={toggle}>Edit your rating!</Tooltip>
                                <BsFillTrashFill ratingId={rating.id} onClick={() => setDeleteRating(rating.id)}/>
                            </div>
                        )}
                        </span>
                    </div>
                ))}
            </div>
            <div>
                {(deleteRating) && (
                    <DeleteRating ratingId={deleteRating} onDelete={() => setDeleteRating(null)}/>
                )}
                {(updateRating) && (
                    <UpdateRating ratingId={updateRating} />
                )}
            </div>
        </div>
    );
}

export default Ratings
