import axios from "axios";
import React, { useState } from "react";

function DeleteRating({ ratingId }) {

  const [message, setMessage] = useState("");

  let handleSubmit = async () => {

    try {
      const username = localStorage.getItem("user");
      let res = await axios.delete(`${"http://localhost/index.php/rating/delete"}?${ratingId}`, {
        method: "DELETE",
        body: JSON.stringify({
          "username": username,
          "id": ratingId
        }),
        headers: {
          "Content-Type": "application/json"
        }
      });
      let resJson = await res.json();
      if (res.status === 200) {
        // setUsername("");
        // setId("");
        setMessage("Rating Deleted");
        // Redirect user to ratings page
        //navigate("/ratingstable");
      } else if (res.status === 400) {
          // Access the error message from backend
          setMessage(resJson.error);
      } else {
        setMessage("Something went wrong");
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="DeleteRating">
      {/* <form onSubmit={handleSubmit}>
        <input
          type="number"
          value={id}
          placeholder="Id"
          onChange={(e) => setId(e.target.value)}
        />
