import axios from "axios";
import React, { useState } from "react";

function DeleteRating({ ratingId }) {

  const [message, setMessage] = useState("");


  let handleSubmit = async () => {
      await axios.delete(`${"http://localhost/index.php/rating/delete"}?${ratingId}`)
      .then(() => setMessage("Rating Successfully deleted!"))
      .catch(() => setMessage("There was a problem, deletion aborted."));
  };

  return (
    <div>
      <div className="DeleteRating">
        <p>Are you sure you want to delete this rating?</p>
        <button onClick={handleSubmit}>Delete</button>
        <button onClick={setMessage("")}>Cancel</button>
        <div className="message">{message ? <p>{message}</p> : null}</div>
      </div>
    </div>
  );
  }

  export default DeleteRating;
