import axios from "axios";
import React, { useState } from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


function DeleteRating({ ratingId }) {

  const [message, setMessage] = useState("");
  const [modal, setModal] = useState(true);



  let handleSubmit = async () => {
      await axios.delete(`${"http://localhost/index.php/rating/delete"}?${ratingId}`)
      .then(() => setMessage("Rating Successfully deleted!"))
      .catch(() => setMessage("There was a problem, deletion aborted."));
      setModal(false);
  };

  return (
    <Modal isOpen={modal}>
    <ModalHeader>Are you sure that you want to delete this rating?</ModalHeader>
    <ModalBody>
      <p>Rating:</p>
    </ModalBody>
    <ModalFooter>
      <Button color="primary" onClick={handleSubmit}>
        Delete
      </Button>{' '}
      <Button color="secondary" onClick={() => setModal(false)}>
        Cancel
      </Button>
    </ModalFooter>
  </Modal>
  );
  }

  export default DeleteRating;
